function parseAndSortData(input) {
    const result = [];
    const lines = input.trim().split('\n');


    lines.forEach(line => {
        if (!line) return; // Bỏ qua các dòng trống


        const parts = line.split(',');
        if (parts.length < 4) return; // Bỏ qua các dòng không đủ phần tử


        const dateTime = parts[0].split(' ');
        if (dateTime.length < 2) return; // Bỏ qua các dòng không có đủ cả ngày và giờ


        let date = dateTime[0];
        let time = dateTime[1];
        const temp = parseFloat(parts[2]);
        const pH = parseFloat(parts[3]);


        // Tạo đối tượng Date từ chuỗi ngày và giờ
        let [year, month, day] = date.split('-').map(Number);
        let [hours, minutes, seconds] = time.split(':').map(Number);
        let currentDate = new Date(year, month - 1, day, hours, minutes, seconds);


        // Giữ lại ngày ban đầu
        let initialDate = currentDate.getDate();


        // Cộng thêm 7 giờ để điều chỉnh cho múi giờ
        currentDate.setHours(currentDate.getHours() + 7);


        // Kiểm tra nếu ngày đã thay đổi sau khi cộng thêm giờ
        if (currentDate.getDate() !== initialDate) {
            // Ngày đã thay đổi, nghĩa là thời gian vượt qua 24h
            year = currentDate.getFullYear();
            month = currentDate.getMonth() + 1; // Tháng trong JavaScript là từ 0-11, cần cộng thêm 1
            day = currentDate.getDate();
        }


        // Lấy giá trị ngày, tháng, năm và thời gian mới sau khi cộng giờ
        let adjustedDate = `${year.toString().padStart(4, '0')}-${month.toString().padStart(2, '0')}-${day.toString().padStart(2, '0')}`;
        let adjustedTime = currentDate.toTimeString().split(' ')[0];


        // Thêm dữ liệu đã phân tích và điều chỉnh vào mảng kết quả
        result.push({date: adjustedDate, time: adjustedTime, temp, pH });
    });


    // Sắp xếp mảng kết quả theo thứ tự giảm dần dựa trên ngày và giờ
    result.sort((a, b) => {
        const dateA = new Date(`${a.date}T${a.time}`).getTime();
        const dateB = new Date(`${b.date}T${b.time}`).getTime();
        return dateB - dateA; // Sắp xếp theo thứ tự giảm dần
    });
    return result;
}


msg.payload = parseAndSortData(msg.payload);


return msg;

function parseAndSortAmountData(input) {
    const result = [];
    const lines = input.trim().split('\n');  // Tách dữ liệu thành các dòng

    lines.forEach(line => {
        if (!line) return; // Bỏ qua các dòng trống

        const parts = line.split(',');
        if (parts.length < 2) return; // Bỏ qua các dòng không đủ phần tử

        const dateTime = parts[0];  // Lấy thời gian trực tiếp từ cột đầu tiên
        const amount = parseFloat(parts[2]);  // Lấy giá trị amount từ cột thứ hai
        var str_amount;
        if (amount == 1) {str_amount = "Minimum"}
        else if (amount == 2) { str_amount = "Medium"}
        else { str_amount = "Maximum" }
        // Sử dụng Date.parse để tạo đối tượng Date
        const currentDate = new Date(Date.parse(dateTime));

        if (isNaN(currentDate)) return; // Bỏ qua nếu thời gian không hợp lệ

        // Lấy giá trị ngày và thời gian sau khi phân tích cú pháp
        const adjustedDate = currentDate.toISOString().split('T')[0];
        const adjustedTime = currentDate.toTimeString().split(' ')[0];

        // Thêm dữ liệu đã phân tích và điều chỉnh vào mảng kết quả
            result.push({ date: adjustedDate, time: adjustedTime, str_amount });
    });

    // Sắp xếp mảng kết quả theo thứ tự giảm dần dựa trên ngày và giờ
    result.sort((a, b) => {
        const dateA = new Date(`${a.date}T${a.time}`).getTime();
        const dateB = new Date(`${b.date}T${b.time}`).getTime();
        return dateB - dateA; // Sắp xếp theo thứ tự giảm dần
    });
    return result;
}

