let indexToRemove = parseInt(flow.get("index"));
let table = flow.get("table").payload;

var msg = {};
// Kiểm tra xem index có hợp lệ không
if (indexToRemove >= 1 && indexToRemove <= table.length) {
    // Xóa phần tử có index tương ứng khỏi bảng
    table = table.filter(item => item.id !== indexToRemove);

    msg.payload = "Delete success!";
}
else {
    msg.payload = "Index out of bounds!"
}
let updated_table = {"payload" : table};
flow.set("table", updated_table);
return msg;

// Đọc dữ liệu JSON từ payload
let data = flow.get("table").payload;


// Sắp xếp dữ liệu theo thời gian (theo định dạng hh:mm)
data.sort((a, b) => {
    let timeA = a.time.split(':').map(Number);
    let timeB = b.time.split(':').map(Number);
    return timeA[0] * 60 + timeA[1] - (timeB[0] * 60 + timeB[1]);
});

// Cập nhật ID cho mỗi dòng dữ liệu
data = data.map((item, index) => ({ ...item, id: index + 1 }));

let table = {"payload": data}
flow.set("table", table);

return table;

// Giả sử timestamp là số milliseconds từ 00:00:00 trong một ngày cụ thể
let timestamp = parseInt(flow.get("time"), 10); // Đảm bảo timestamp là số nguyên

// Tính toán giờ và phút
let hours = Math.floor(timestamp / (1000 * 60 * 60));
let minutes = Math.floor((timestamp % (1000 * 60 * 60)) / (1000 * 60));

// Định dạng thành chuỗi hh:mm
let formattedTime = (hours < 10 ? '0' : '') + hours + ':' + (minutes < 10 ? '0' : '') + minutes;

// Gán chuỗi thời gian đã định dạng vào msg.payload
msg.payload = formattedTime; 

return msg;

